export default class Node{
	constructor(key, center, type,target){
		this.key = key;//uuid
		this.selected = false;
		this.type = 'line';//type: line, curve, smooth, offcurve
		this.center = center;// ( x, y )
		this.view = undefined;
		this.target = target;//append to svg
		this.parent = undefined;
		this.makeNode();

	}

	setParent(parent) {
		this.parent = parent;
	}
	makeRect(){
		let width = 10,
			height = width,
			helfWidth = width/2,
			x = this.center.x - helfWidth,
			y = this.center.y - helfWidth;

		return {width, height, x, y};
	}
	makeNode() {

		let	rect = this.makeRect(),
			view = this.target.rect(rect.x, rect.y, 
				rect.width, rect.height);

		view.attr({
			id: this.key,
			'class':'node'
		});

		this.view = view;
	}
	moveTo(point){
		this.center = point;
		let rect = this.makeRect();
		this.view.attr(
			{
				'x': rect.x,
				'y': rect.y
			});
	}
	toggleSelected(){
		this.setSelected(!this.selected);
	}
	setSelected(selected){
		this.selected = selected;
		this.view.attr('fill', selected?'#6699ff':'none');
	}
	updateBesideSegment(){
		let nodeIndex = this.parent.nodes.indexOf(this);
		this.parent.updateSegment(nodeIndex);
	}
}

import interact from 'interact.js'; 
import {DrawTool} from './Tool';

export default class NodeController{
	constructor(target){
		this.target = target;//append to svg
		this.nodeMap = new Map();
		this.idIndex = 0;
		this.pathIndex = 0;
		this.tool = new DrawTool();
		this.tool.setDelegate(this);
		this.selectedNodes = [];
		this.setListener();
	}
	setListener() {
		interact('#drawing_panel')
			.on('tap', this.tapBackground.bind(this));
		interact('.node')
			.draggable({onmove:this.dragNode.bind(this)})
			.on('doubletap', this.doubleTapNode.bind(this))
			.on('tap', this.tapNode.bind(this));//不bind的話nodeTap裡的this會變別人
	}
	getNode(event) {
		return this.nodeMap.get(event.target.id);
	}
	dragNode(event){
		if (event.target.classList[0] !== 'node') return;
		this.tool.dragNode(event);	
	}

	tapNode(event){
		if (event.target.classList[0] !== 'node') return;
		this.tool.tapNode(event);
	}
	doubleTapNode(event) {
		if (event.target.classList[0] !== 'node') return;
		this.tool.doubleTapNode(event);
	}
	tapBackground(event){
		if (event.target.nodeName !== 'svg') return;
		this.tool.tapBackground(event);
	}
	cleanSelectedNodes(){
		for (var i = this.selectedNodes.length - 1; i >= 0; i--) {
			this.selectedNodes[i].setSelected(false);
		}
		this.selectedNodes.splice(0, this.selectedNodes.length);
	}

	getKey(index) {
		return `n-${index}`;
	}
	positionOnDrawingPanel(event) {
		let svgtag = document.getElementById('drawing_panel'),
			x = event.clientX - svgtag.getBoundingClientRect().top,
			y = event.clientY - svgtag.getBoundingClientRect().left;
		return {x:Math.floor(x), y:Math.floor(y)};
	}
}

export default class Path {
	constructor(key, target, nodes){
		this.target = target;//append to svg
		this.nodes = nodes||[];//optional
		this.key = key;
		this.closed = false;

		this.segmnetMap = new Map();
	}

	addNode(node){
		node.setParent(this);
		this.nodes.push(node);


		if (this.noSegment()) return;
		let lastOne = this.nodes.length - 1;
		this.drawLineSegment(this.nodes[lastOne-1],
			this.nodes[lastOne]);
	}

	noSegment(){
		return (this.nodes.length <= 1);
	}
	makePathDescription(){
		let path_description = '',
			flag;
		for (var i = 0; i < this.nodes.length; i++) {
			if(i===0) flag = 'M';

			else if(this.nodes[i].type === 'line') flag = ' L';


			path_description +=`${flag}${this.nodes[i].center.x} ${this.nodes[i].center.y}`;
		}

		if (this.closed) path_description+='Z';

		return path_description;
	}

	drawPath(){
		this.view.attr('d', this.makePathDescription());
	}
	drawLineSegment(p1, p2){
		let d = this.makeLineDescript(p1, p2),
			segment = this.target.path(d),
			segmentKey = `${this.key}-${this.nodes.indexOf(p1)}`;
		segment.attr({
			id:segmentKey,
			class:'segment'
		});
		this.segmnetMap.set(segmentKey, segment);
	}
	makeLineDescript(p1, p2){
		//Mx1 y1 L x2 y2
		return `M ${p1.center.x} ${p1.center.y}`
			+` L ${p2.center.x} ${p2.center.y}`;
	}
	updateSegment(index){
		if(!this.closed){
			if(index === 0) this.updatePresentSegment(index);
			else if(index === this.nodes.length-1) this.updatePrevSegment(index);
		}
		else{
			this.updatePrevSegment(index);
			this.updatePresentSegment(index);
		}
	}

	updatePrevSegment(present){
		let	size = this.nodes.length,
			prev = (present - 1 + size)%size,
			prevSeg = this.segmnetMap.get(`${this.key}-${prev}`),
			prev_d = this.makeLineDescript(this.nodes[prev], this.nodes[present]);
		prevSeg.attr('d', prev_d);
	}

	updatePresentSegment(present){
		let size = this.nodes.length,
			next = (present + 1 + size)%size,
			presentSeg = this.segmnetMap.get(`${this.key}-${present}`),
			present_d = this.makeLineDescript(this.nodes[present], this.nodes[next]);
		presentSeg.attr('d', present_d);

	}
	firstNode(){
		return this.nodes[0];
	}

	lastNode(){
		return this.nodes[this.nodes.length-1];
	}
	clearSelected(){
		for(let node of this.nodes){
			node.setSelected(false);
		}
	}
	closePath(){
		this.closed = true;
		this.drawLineSegment(this.lastNode(), this.firstNode());
	}
}

import Path from './Path';
import Node from './Node';
import _ from 'lodash/core';
class Tool{
	constructor(){}

	setDelegate(delegate){
		this.delegate = delegate;
	}
	tapNode(){}

	tapBackground(){
	}

	doubleTapNode(){}

	dragNode(){}
}

class DrawTool extends Tool{
	constructor(){
		super();
		this.handle = undefined;

	}

	tapNode(event){
		if (!this.handle) return;

		let node = this.delegate.getNode(event),
			firstNode = this.handle.firstNode();

		if (this.handle.nodes.length > 2 && node === firstNode) {
			this.handle.closePath();
			this.handle = undefined;
		}
	}

	tapBackground(event){
		let point = this.delegate.positionOnDrawingPanel(event);
		if (!this.handle) {
			this.makePath(point);	
		}

		this.addNodeToHandle(point);
	}

	makePath(){
		this.handle = new Path(`p-${this.delegate.pathIndex}`, this.delegate.target);
		this.delegate.pathIndex++;
	}

	makeLineNode(point) {
		let key = this.delegate.getKey(this.delegate.idIndex),
			node = new Node(key, point, 'line', this.delegate.target);
		this.delegate.nodeMap.set(key, node);
		this.delegate.idIndex++;

		return node;
	}

	addNodeToHandle(point){
		let	node = this.makeLineNode(point);
		this.handle.addNode(node);
	}
}

class SelectTool extends Tool{

	tapNode(event){
		let node = this.delegate.getNode(event);
		this.delegate.selectedNodes.push(node);
		node.toggleSelected();
	}

	tapBackground(){
		this.delegate.cleanSelectedNodes();
	}

	doubleTapNode(event){}

	dragNode(event){
		let node = this.delegate.getNode(event),
			x = node.center.x + event.dx,
			y = node.center.y + event.dy;
		node.moveTo({x, y});
		node.updateBesideSegment();
	}
}

export {Tool, DrawTool, SelectTool};

import Snap from 'snapsvg';
import NodeController from './NodeController';
import {DrawTool, SelectTool} from './Tool';
let svg = Snap('#drawing_panel');
let nodeController = new NodeController(svg);
initSVG();
setKeyUP();

function initSVG(){
	svg.attr({
		width:800,
		height:600
	});
}

function setKeyUP(){

	let toolHandler = new Map(),
		select = new SelectTool(),
		pen = new DrawTool();
	select.setDelegate(nodeController);
	pen.setDelegate(nodeController);

	toolHandler.set(32,  select);//space => select tool
	toolHandler.set(80, pen);//Pp => path tool (drawing tool)
	window.onkeyup = function(e) {
		var key = e.keyCode ? e.keyCode : e.which;
		nodeController.tool = toolHandler.get(key);
	};
}
